// 处理Gemini直接生成的HTML结果
const response = $input.first().json;
let generatedHTML = response.candidates[0].content.parts[0].text;

// 清理生成的HTML
generatedHTML = generatedHTML.replace(/```html\s*/gi, '').replace(/```\s*$/gi, '');
generatedHTML = generatedHTML.replace(/```\s*/g, '').replace(/```\s*$/g, '');
generatedHTML = generatedHTML.trim();

// 强制压缩HTML
generatedHTML = generatedHTML.replace(/\r\n/g, ' ').replace(/\n/g, ' ').replace(/\r/g, ' ');
generatedHTML = generatedHTML.replace(/\t/g, ' ');
generatedHTML = generatedHTML.replace(/\s+/g, ' ');
generatedHTML = generatedHTML.replace(/> </g, '><');
generatedHTML = generatedHTML.replace(/;\s+/g, ';').replace(/:\s+/g, ':').replace(/,\s+/g, ',');
generatedHTML = generatedHTML.replace(/\/\*[^*]*\*+(?:[^/*][^*]*\*+)*\//g, '');
generatedHTML = generatedHTML.trim();

// 创建文件名
const timestamp = new Date().toISOString().slice(0, 19).replace(/[:.]/g, '-');
const originalFileName = $('Prepare PDF Analysis').first().json.fileName || 'financial-report';
const cleanFileName = originalFileName.replace(/\.pdf$/i, '').replace(/[^a-zA-Z0-9-_]/g, '-');
const filename = `${cleanFileName}-analysis-${timestamp}.html`;

// 创建下载链接
const dataUrl = 'data:text/html;charset=utf-8,' + encodeURIComponent(generatedHTML);

// 计算压缩效果
const originalSize = response.candidates[0].content.parts[0].text.length;
const compressedSize = generatedHTML.length;
const compressionRatio = ((originalSize - compressedSize) / originalSize * 100).toFixed(1);

return {
  json: {
    html: generatedHTML,
    filename: filename,
    downloadUrl: dataUrl,
    status: 'success',
    message: '🎉 PDF财报分析网页生成成功！腾讯蓝主题+Bento Grid设计',
    processingInfo: {
      method: 'Direct PDF to HTML via Gemini 2.0',
      originalFileName: originalFileName,
      outputFileName: filename,
      processingTime: 'Single API call'
    },
    compressionInfo: {
      originalSize: originalSize,
      compressedSize: compressedSize,
      compressionRatio: compressionRatio + '%',
      note: '已移除所有换行符和多余空白'
    },
    instructions: {
      method1: '直接复制上面html字段的内容，粘贴到.html文件',
      method2: '点击downloadUrl链接，右键保存网页',
      note: '包含PDF分析+Framer Motion动效+TailwindCSS样式+财报数据可视化'
    },
    preview: generatedHTML.substring(0, 200) + '...'
  }
};

// 准备PDF文件和完整的prompt，一步生成HTML
const formData = $input.first().json;
const binaryData = $input.first().binary;

const extraRequirements = formData.extraRequirements || '';

console.log('Form data keys:', Object.keys(formData));
console.log('Binary data available:', !!binaryData);
console.log('Binary data keys:', binaryData ? Object.keys(binaryData) : 'None');
console.log('Extra requirements:', extraRequirements);

// 检查是否有PDF文件
if (!binaryData || Object.keys(binaryData).length === 0) {
  throw new Error('请上传PDF文件！');
}

const pdfKey = Object.keys(binaryData)[0];
const pdfInfo = binaryData[pdfKey];

console.log('PDF Info:', {
  fileName: pdfInfo.fileName,
  mimeType: pdfInfo.mimeType,
  dataLength: pdfInfo.data ? pdfInfo.data.length : 0
});

// 构造完整的prompt，包含财报分析和HTML生成
let completePrompt = `You are an expert financial analyst and web developer. Please analyze the uploaded PDF financial report and create a comprehensive, professional HTML webpage based on the analysis.

📋 TASK:
1. Analyze all financial data in the PDF document
2. Extract key metrics, trends, and insights
3. Generate a complete HTML webpage with the analyzed data

🎨 DESIGN REQUIREMENTS:
1. Use Bento Grid visual design style with pure black background and Tencent blue color #006EFF for highlights
2. Emphasize super large fonts or numbers to highlight key points, create visual contrast between large and small elements
3. Mix Chinese and English - Chinese in large bold fonts, English as accent text
4. Simple line graphics for data visualization and decorative elements
5. Use highlight color transparency gradients for tech feeling, but different highlight colors should not gradient into each other
6. Mimic Apple website animations, scroll-triggered effects
7. Data can reference online chart components with consistent styling
8. Use Framer Motion (via CDN)
9. Use HTML5, TailwindCSS 3.0+ (via CDN) and necessary JavaScript
10. Use professional icon libraries like Font Awesome or Material Icons (via CDN)
11. Avoid using emoji as main icons
12. Do not omit any content points from the financial analysis

📊 CONTENT FOCUS:
- Revenue figures and growth trends
- Profit margins and financial ratios
- Business segment performance
- Strategic initiatives and highlights
- Comparative data and year-over-year changes
- Key operational metrics
- Future outlook and guidance`;

// 添加用户的额外需求
if (extraRequirements && extraRequirements.trim()) {
  completePrompt += `

🎯 ADDITIONAL USER REQUIREMENTS:
${extraRequirements}`;
}

completePrompt += `

⚡ IMPORTANT OUTPUT REQUIREMENTS:
Generate MINIFIED HTML code in a single line without line breaks or unnecessary whitespace. The HTML should be production-ready and compressed. Include all CDN links, responsive design, smooth animations, and comprehensive financial data presentation. 

Remember: Output ONLY the compressed single-line HTML code ready for production use. Do not include markdown code blocks or explanations.`;

return {
  json: {
    completePrompt: completePrompt,
    extraRequirements: extraRequirements,
    fileName: pdfInfo.fileName,
    step: 'direct_pdf_to_html'
  },
  binary: binaryData
};
